var all=document.getElementsByTagName("*");var slides=[];var cur=0;for(i in all){if((" "+all[i].className+" ").indexOf(" slide ")>-1){all[i].style.display="none";slides.push(all[i])}}slides[0].style.display="block";next=function(){if(cur<slides.length-1){slides[cur].style.display="none";slides[cur+1].style.display="block";cur+=1}};prev=function(){if(cur>0){slides[cur].style.display="none";slides[cur-1].style.display="block";cur-=1}};document.onclick=function(a){a.pageX>window.innerWidth/2&&next();a.pageX<window.innerWidth/2&&prev()};document.onkeydown=function(a){(a.which===37||a.which===38)&&prev();(a.which===39||a.which===40)&&next()}